import { useRecoilState } from "recoil";
import { homeClubTabState, clubsState } from "../store";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";
import { readAllClubs, readSomeClubs } from "../api/club";

axios.defaults.baseURL = "http://cbnucore.site";

export default function Promotion() {
  const [posts, setPosts] = useRecoilState(clubsState);
  const [homeTab, setHomeTab] = useRecoilState(homeClubTabState);
  const navigate = useNavigate();
  const [count, setCount] = useState([0, 0]);

  const countPosts = () => {
    let i = 0;
    let j = 0;
    readAllClubs(0).then((res) => {
      res.data.forEach((post) => (post.classification === 1 ? ++i : ++j));
      setCount([i, j]);
    });
  };

  const getPosts = () => {
    readSomeClubs(0, 16, homeTab).then((res) => setPosts(res.data));
  };

  useEffect(() => {
    getPosts();
    countPosts();
    document.title = `${posts.length}개의 동아리가 함께 하고 있습니다 | Core`;
  }, [homeTab]);

  return (
    <div className={"w-full min-w-home2"}>
      <div className={""} />
      <div className={" w-full bg-main_mid text-white px-[64px] py-[56px]"}>
        <p className={"text-h1 font-light"}>
          동아리 <span className={"font-bold"}>홍보게시판</span>
        </p>
        <div className={"mt-[10px]"} />
        <p className={"text-h3"}>
          동아리 홍보 및 신입 부원 모집 공고를 둘러보세요!{" "}
        </p>
        <div className={"mt-[32px]"} />
        <ul className={"list-disc list-inside"}>
          <li>중앙 동아리 {count[0]}개</li>
          <li>직무 동아리 {count[1]}개</li>
        </ul>
      </div>
      <div className={"px-[63px]"}>
        <div className={"flex"}>
          <ClubTab />
        </div>

        <article className={""}>
          <div className={"grid grid-cols-4 gap-10"}>
            {posts.map((post) => {
              return (
                <div
                  className={
                    "w-[200px] h-[400px] 2xl:w-[300px] 2xl:h-[440px] rounded-xl shadow-lg transition hover:scale-110"
                  }
                  onClick={() => {
                    navigate("/clubdetail/" + post._id);
                  }}
                >
                  <div
                    className={
                      "w-[200px] h-[300px] 2xl:w-[300px] 2xl:h-[330px]"
                    }
                  >
                    <Image post={post} />
                  </div>
                  <div className={"w-[200px] h-[100px] 2xl:w-[300px] 2xl:h-[110px]"}>
                    <Content post={post} />
                  </div>
                </div>
              );
            })}
          </div>
        </article>
      </div>
    </div>
  );
}

const ClubTab = () => {
  const [homeTab, setHomeTab] = useRecoilState(homeClubTabState);
  const navigate = useNavigate();

  const onClickHandler = (tabValue) => {
    setHomeTab(tabValue);
  };

  return (
    <>
      <div>
        <div className={"mt-[43px]"} />
        <div className={"flex gap-3"}>
          <button
            className={homeTab === 0 ? "btn-home-tab-focused" : "btn-home-tab"}
            onClick={() => onClickHandler(0)}
          >
            전체
          </button>
          <button
            className={homeTab === 1 ? "btn-home-tab-focused" : "btn-home-tab"}
            onClick={() => onClickHandler(1)}
          >
            중앙 동아리
          </button>
          <button
            className={homeTab === 2 ? "btn-home-tab-focused" : "btn-home-tab"}
            onClick={() => onClickHandler(2)}
          >
            직무 동아리
          </button>
          <div className={""}>
            <form
              className={
                "flex h-[40px] w-[300px] rounded-full border border-gray2"
              }
              onSubmit={(e) => {
                // e.preventDefault();
                navigate("/club/search/?query=" + e.target.query.value);
                setHomeTab(0); // 기본 홈탭을 0으로 바꾸기
              }}
            >
              <input
                type={"text"}
                className={"rounded-full w-[250px] px-3 outline-0"}
                placeholder={"동아리를 검색해보세요."}
                name={"query"}
              />
              <button type={"submit"}>
                <i className="fa-solid fa-magnifying-glass fa-xl pt-4 ml-2"></i>
              </button>
            </form>
          </div>
        </div>
        <div className={"mt-[32px]"} />
      </div>
    </>
  );
};

const Image = (props) => {
  return (
    <div className={"2xl:w-[300px] 2xl:h-[200px]"}>
      <img src={props.post.image_url} alt="img" className={"rounded-t-lg"} />
    </div>
  );
};

const Content = (props) => {
  return (
    <div className={"p-2"}>
      <div className={"2xl:flex"}>
        <div
          className={
            "text   w-[115px] 2xl:w-[130px] 2xl:text-lg text-center 2xl:ml-3 text-md border border-darkgray text-darkgray rounded-xl px-3"
          }
        >
          {props.post.classification === 0 ? "중앙 동아리" : "직무 동아리"}
        </div>
        <div className={"text-lg 2xl:text-xl font-bold"}>
          {props.post.title}
        </div>
      </div>
      <div className={"hidden 2xl:block"}>
        {props.post.content.length > 35
          ? props.post.content.slice(0, 35) + "..."
          : props.post.content}
      </div>
      <div className={"block 2xl:hidden text-sm"}>
        {props.post.content.length > 12
          ? props.post.content.slice(0, 12) + "..."
          : props.post.content}
      </div>
    </div>
  );
};
